
function resetSearch(){$('#quarx-search').val('');$('#quarx-search').css('color','#222');}
function profileImageResize(){var width=$('.quarx-profile-image-box img').width(),height=$('.quarx-profile-image-box img').height();if(height==width){$('.quarx-profile-image-box img').css({marginTop:15,marginLeft:($('.quarx-profile-image-box img').parent().width()-320)/2,height:320,width:320});}
else if(height>width){var newWidth=(width*320)/height,marginLeft=($('.quarx-profile-image-box img').parent().width()-newWidth)/2,marginTop=(350-320)/2;$('.quarx-profile-image-box img').css({marginTop:marginTop,marginLeft:marginLeft,height:320,width:newWidth});}
else if(width>height){var newHeight=(height/width)*350,marginTop=(350-newHeight)/2,marginLeft=($('.quarx-profile-image-box img').parent().width()-350)/2;$('.quarx-profile-image-box img').css({marginTop:marginTop,marginLeft:marginLeft,width:320,height:newHeight});}}
function thumbnailImageResize(){$('.quarx-img-thumb-holder img').each(function(){var width=$(this).width(),height=$(this).height();if(height===width){$(this).css({marginTop:-10,marginLeft:0,height:114,width:114});}
if(height>width){var newWidth=(width*114)/height,marginLeft=(114-newWidth)/2,marginTop=-10;$(this).css({marginTop:marginTop,marginLeft:marginLeft,height:114,width:newWidth});}
if(width>height){var newHeight=(height/width)*114,marginTop=-13,marginLeft=0;$(this).css({marginTop:marginTop,marginLeft:marginLeft,width:114,height:newHeight});}});}
function dialogDestroy(idTag){$(idTag+'_dialog-header').remove();$(idTag+' .dialogbox_body').remove();$(idTag).hide();$('#quarx-modal').fadeOut();}
function inputDialogDestroy(idTag){$(idTag+'_dialog-header').remove();$(idTag+' .dialogbox_body_btns').remove();$(idTag).hide();$('#quarx-modal').fadeOut();}
